mapper.insert:
/* com.project.toy.points.mapper.PointsMapper, 활동점수 적립 */

INSERT INTO T_POINTS ( POINTS_CD, POINTS, USER_ID )
VALUES ( #{ pointsCd }, #{ points }, #{ userId } )

mapper.select:
/* com.project.toy.points.mapper.PointsMapper, 하루단위 첫 로그인 판별 */

SELECT COUNT(1)
FROM   T_POINTS
WHERE  1=1
AND    POINTS_CD = #{ pointsCd }
AND    DATE_FORMAT(I_DATE, "%Y-%m-%d") = CURDATE()  
AND    USER_ID = #{ userId }

/* com.project.toy.points.mapper.PointsMapper, 소멸예정 활동점수 */
SELECT A.USER_NO, A.USER_ID, A.USER_NICKNAME, IFNULL(FORMAT(SUM(C.POINTS), 0), 0) AS POINTS FROM T_PERSON A LEFT OUTER JOIN T_AUTH_MODEL B ON A.ROLE = B.MODEL_NAME LEFT OUTER JOIN T_POINTS C ON A.USER_ID = C.USER_ID WHERE 1=1
AND C.I_DATE <= DATE_ADD(NOW(), INTERVAL -6 MONTH)
AND A.USER_ID = #{ userId }
