mapper.select:
/* com.project.toy.user.dto.LockUserDTO */

SELECT USER_ID, FAIL_CNT, LOCK_YN, LOCK_DATE
FROM   T_PERSON
WHERE  1=1
AND    USER_ID = #{ userId }
AND    USE_YN = 'Y'

mapper.update:
		/* com.project.toy.user.dto.LockUserDTO */

		UPDATE T_PERSON
		SET    FAIL_CNT = #{ failCnt }
		     , LOCK_YN = #{ lockYn }

     	     , LOCK_DATE = NOW()

     	WHERE  1=1
     	AND    USER_ID = #{ userId }
     	AND    USE_YN = 'Y'
