mapper.select:
/* com.project.toy.user.dto.LockUserDTO */

SELECT USER_ID, FAIL_CNT, LOCK_YN, LOCK_DATE
FROM   T_PERSON
WHERE  1=1
AND    USER_ID = #{ userId }
AND    USE_YN = 'Y'

mapper.update:
		/* com.project.toy.user.dto.LockUserDTO */

		UPDATE T_PERSON
		SET    FAIL_CNT = #{ failCnt }
		     , LOCK_YN = #{ lockYn }

     	     , LOCK_DATE = NOW()

     	WHERE  1=1
     	AND    USER_ID = #{ userId }
     	AND    USE_YN = 'Y'

/* com.project.toy.user.dto.UserDTO */

UPDATE T_PERSON
SET    USER_PWD = #{ userPwd }
     , PWD_U_DATE = NOW()
WHERE  1=1
AND    USER_ID = #{ userId }
AND    USE_YN = 'Y'

/* com.project.toy.user.dto.UpdateUserDTO */

UPDATE T_PERSON
SET    USER_NICKNAME = #{ userNickname }
     , USER_EMAIL = #{ userEmail }
     , PWD_U_DATE = NOW()
WHERE  1=1
AND    USER_ID = #{ userId }
AND    USE_YN = 'Y'

/* com.project.toy.user.dto.CertifiedUserDTO */

UPDATE T_PERSON_CERTIFIED
SET    CERTIFIED_YN = 'Y'
     , U_DATE = NOW()
WHERE  1=1
AND    USER_ID = #{ userId }

/* com.project.toy.user.dto.UserDTO */

UPDATE T_PERSON
SET    ROLE = 'USER'
     , U_DATE = NOW()
WHERE  1=1
AND    USER_ID = #{ userId }
